css:
/* Custom global Properties */

.border-bottom {
    border-bottom: thin solid #d1d8dd;
}

.border-top {
    border-top: thin solid black;
}


/* Override default themes */
.print-heading {
    text-align: center;
    padding-bottom: 0;
    border-bottom: 0;
    font-weight: bold;
}

.print-heading h2 {
    font-weight: bold;
}

.row .data-field {
    margin-right: 0;
    margin-bottom: 10px;
}


/* Custom CSS */
.invoice-id-text, .invoice-id-number {
    border: solid;
    border-width: thin;
}

.invoice-id-text {
    border-color: transparent;
    text-align: right;
}
