css:
/* Custom global Properties */
.pr-0 {
    padding-right: 0;
}

.border-bottom {
    border-bottom: thin solid #d1d8dd;
}


/* Override default themes */
.print-heading {
    text-align: center;
    padding-bottom: 0;
    border-bottom: 0;
    font-weight: bold;
}

.print-heading h2 {
    font-weight: bold;
}

.row .data-field {
    margin-right: 0;
    margin-bottom: 10px;
}

.table-sr {
    display: none;
}

div[data-fieldname="items"] > table {
    margin-top: 10px;
    margin-bottom: 0;
}

tr > th, /* Align all header. also override some of the columns cells that has text-right */
tr > td:nth-child(6),
tr > td:nth-child(10) {
    text-align: center !important;
}

tr > td {
    text-align: center;
}


/* Custom CSS */
.invoice-id-text, .invoice-id-number {
    border: solid;
    border-width: thin;
}

.invoice-id-text {
    border-color: transparent;
    text-align: right;
}

.total-table {
    width: 100%;
    margin-top: 0;
}

.total-table td:first-child {
    width: 88%;
}
